sp_widget.client_script:
function($rootScope, $scope) {

  var c = this;
	c.selectedItemIndex = null;

	c.selectedItem = function(index) {
		c.selectedItemIndex = index;
		var id = c.data.notes[index].sys_id;
		$rootScope.$emit('selectNote', id);
	};

	$rootScope.$on('updateList', function(event,data) {
		c.data.notes[c.selectedItemIndex].title = data.title;
		c.data.notes[c.selectedItemIndex].shortNote = data.shortNote;
	});

}

sp_widget.script:
(function() {
	//create an array to populate with notes
	data.notes = getNotes();

})();

function getNotes() {
	var notes = [];
	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user', gs.getUserID());
	gr.orderByDesc('sys_created_on');
	gr.query();

	while (gr.next()) {
		var note = {};
		$sp.getRecordDisplayValues(note, gr, 'number,title,sys_id');
		note.shortNote = gr.getValue('note').slice(0,20);
		notes.push(note);
	}

	return notes;
}
